import React from "react";

function Vaccine() {
  const [input, setInput] = React.useState("");
  const [Otp, setOtp] = React.useState("");
  const apihitfun = async () => {
    const ress=await fetch("/api", {
      "method": "POST",
      headers: {
          "content-type": "application/json",
          "accept": "application/json"
        },
       body: JSON.stringify({ number: input })
    })
     const res=await ress.json();
     setInput
     console.log(res);
  };
  const otp = async () => {
    const ress=await fetch("/validateOtp", {
      "method": "POST",
      headers: {
          "content-type": "application/json",
          "accept": "application/json"
        },
       body: JSON.stringify({ otp: input })
    })
     const res=await ress.json();
     console.log(res);
  };

  return (
    <>
      <div className="Vaccinebodyclass">
        <label for="fname">
          <strong>Mobile Number:</strong>
        </label>
        <input value={input} onInput={(e) => setInput(e.target.value)} />
        <button onClick={() => apihitfun()}>generate otp</button>
        <input value={input} onInput={(e) => setInput(e.target.value)} />
        <button onClick={() => otp()}>submit</button>
      </div>
    </>
  );
}
export default Vaccine;
